import { useEffect, useState } from "react";
import Popup from "reactjs-popup";


export default function GUI() {

  const [test_data, setData] = useState(null);
  const [userName, setName] = useState('');
  const [userId, setID] = useState(null);

  useEffect(() => {
    fetch("/api").then((res) => res.json()).then((data) => setData(data.message));
    fetch("/getID").then((res) => res.json()).then((data) => {
      setID(data.message);
    });
  }, []);

  function setUserName() {
    setName(document.getElementById("username_text").textContent);
    // const name = prompt("Enter your new name");
    // setName(name);
    fetch("/createUser", {
      method: "POST",
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      },
      body: JSON.stringify({
        userId: userId,
        userName: name,
      }),
    }).then((res) => res.json()).then((data) => console.log(data));
  }

  return (
    <>
      <div id="chat_display">
        <p id="chat_log">{!test_data ? "Waiting for Server response" : test_data}</p>
      </div>
      <div id="button_bar">
        <Popup trigger={<button id="set_user">Set User Name</button>}>
          <div>
            <label>
              Enter your username:
              <textarea name="username_text" rows={1} cols={23}></textarea>
            </label>
            <div>
              <button onClick={setUserName}>
                Save
              </button>
              <button>
                Cancel
              </button>
            </div>
          </div>
        </Popup>
        <button>
          Create Chatroom
        </button>
        <button>
          Join Chatroom
        </button>
      </div>
      <div id="input_field">
        <p>ID is: {userId}</p>
        <p>Username is: {userName}</p>
        <label>
          Write message below:
          <textarea name="input_text" rows={8} cols={100}></textarea>
        </label>
      </div>
    </>
  );
}
